package resource;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		                                                                                 
		int num=5;
		int num1=1;
		for(int i=1;i<=num;i++) {
			
			num1=num1*i;
			System.out.println(num1);
		}
	
	}

}
